def get_departments(employees):
    """Получить список всех отделов"""
    departments = []
    for emp in employees:
        if emp['отдел'] not in departments:
            departments.append(emp['отдел'])
    return departments

def get_average_salary(employees):
    """Рассчитать средний оклад"""
    if not employees:
        return 0
    total = sum(emp['оклад'] for emp in employees)
    return total / len(employees)